import { Component } from '@angular/core';

@Component({
  selector: 'app-tabuleiro',
  templateUrl: './tabuleiro.component.html',
  styleUrls: ['./tabuleiro.component.css'],
})
export class TabuleiroComponent {
  estadoTabuleiro: any[][] = [];

  ngOnInit() {
    this.colorirCasas();
    this.inicializaTabuleiro();
  }

  colorirCasas(){
    const casasEscuras =
      ['b8', 'd8', 'f8', 'h8'
      ,'a7', 'c7', 'e7', 'g7'
      , 'b6', 'd6', 'f6', 'h6'
      , 'a5', 'c5', 'e5', 'g5'
      , 'b4', 'd4', 'f4', 'h4'
      , 'a3', 'c3', 'e3', 'g3'
      , 'b2', 'd2', 'f2', 'h2'
      , 'a1', 'c1', 'e1', 'g1'];

    const casasClaras =
      ['a8', 'c8', 'e8', 'g8'
      , 'b7', 'd7', 'f7', 'h7'
      , 'a6', 'c6', 'e6', 'g6'
      , 'b5', 'd5', 'f5', 'h5'
      , 'a4', 'c4', 'e4', 'g4'
      , 'b3', 'd3', 'f3', 'h3'
      , 'a2', 'c2', 'e2', 'g2'
      , 'b1', 'd1', 'f1', 'h1'];

    const casas = document.querySelectorAll('.casa');

    casas.forEach((casa: Element) =>{
      if (casasClaras.includes(casa.id)){
        (casa as HTMLElement).style.backgroundColor = '#FFFFE0';
      }

      if (casasEscuras.includes(casa.id)){
        (casa as HTMLElement).style.backgroundColor = '#008000';
      }
    })
  }

  inicializaTabuleiro() {
    this.estadoTabuleiro = [
      ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
      ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
      ['', '', '', '', '', '', '', ''],
      ['', '', '', '', '', '', '', ''],
      ['', '', '', '', '', '', '', ''],
      ['', '', '', '', '', '', '', ''],
      ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
      ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R'],
    ];
  }

  getImagensPecas(peca: string): string {
    const imagens: {[key: string]: string} = {
      'k': 'static/tabuleiro/pecas/pretas-rei.png',
      'q': 'static/tabuleiro/pecas/pretas-rainha.png',
      'r': 'static/tabuleiro/pecas/pretas-torre.png',
      'b': 'static/tabuleiro/pecas/pretas-bisbo.png',
      'n': 'static/tabuleiro/pecas/pretas-cavalo.png',
      'p': 'static/tabuleiro/pecas/pretas-peao.png',
      'K': 'static/tabuleiro/pecas/brancas-rei.png',
      'Q': 'static/tabuleiro/pecas/brancas-rainha.png',
      'R': 'static/tabuleiro/pecas/brancas-torre.png',
      'B': 'static/tabuleiro/pecas/brancas-bisbo.png',
      'N': 'static/tabuleiro/pecas/brancas-cavalo.png',
      'P': 'static/tabuleiro/pecas/brancas-peao.png'
    };

    return imagens[peca];
  }

}
