import { Component } from '@angular/core';

@Component({
  selector: 'app-tabuleiro',
  templateUrl: './tabuleiro.component.html',
  styleUrls: ['./tabuleiro.component.css'],
})
export class TabuleiroComponent {
  ngOnInit() {
    this.colorirCasas();
  }

  colorirCasas(){
    const casasEscuras =
      ['b8', 'd8', 'f8', 'h8'
      ,'a7', 'c7', 'e7', 'g7'
      , 'b6', 'd6', 'f6', 'h6'
      , 'a5', 'c5', 'e5', 'g5'
      , 'b4', 'd4', 'f4', 'h4'
      , 'a3', 'c3', 'e3', 'g3'
      , 'b2', 'd2', 'f2', 'h2'
      , 'a1', 'c1', 'e1', 'g1'];

    const casasClaras =
      ['a8', 'c8', 'e8', 'g8'
      , 'b7', 'd7', 'f7', 'h7'
      , 'a6', 'c6', 'e6', 'g6'
      , 'b5', 'd5', 'f5', 'h5'
      , 'a4', 'c4', 'e4', 'g4'
      , 'b3', 'd3', 'f3', 'h3'
      , 'a2', 'c2', 'e2', 'g2'
      , 'b1', 'd1', 'f1', 'h1'];

    const casas = document.querySelectorAll('.casa');

    casas.forEach((casa: Element) =>{
      if (casasClaras.includes(casa.id)){
        (casa as HTMLElement).style.backgroundColor = '#FFFFE0';
      }

      if (casasEscuras.includes(casa.id)){
        (casa as HTMLElement).style.backgroundColor = '#008000';
      }
    })
  }

}
